import React, { useState, useEffect } from 'react';
import { collection, query, where, onSnapshot } from "firebase/firestore";
import { dbService, authService } from '../../firebase/fbInstance';
import CalendarHead from './CalendarHead';
import CalendarBody from './CalendarBody';
import TodoModal from './TodoModal';

const Calendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    if (!authService.currentUser) return;

    const startOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
    const endOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);

    const q = query(
      collection(dbService, "todos"),
      where("userId", "==", authService.currentUser.uid),
      where("date", ">=", startOfMonth.toISOString().split('T')[0]),
      where("date", "<=", endOfMonth.toISOString().split('T')[0])
    );

    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const fetchedTodos = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setTodos(fetchedTodos);
    });

    return () => unsubscribe();
  }, [currentDate]);

  const handleDateClick = (date) => {
    setSelectedDate(date);
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
  }

  return (
    <div className="calendar-container flex flex-col items-center w-full max-w-4xl mx-auto h-full min-h-[800px]">
      <CalendarHead currentDate={currentDate} setCurrentDate={setCurrentDate} />
      <CalendarBody 
        currentDate={currentDate} 
        onDateClick={handleDateClick}
        todos={todos}
      />
      <TodoModal
        isOpen={modalOpen}
        onClose={handleCloseModal}
        date={selectedDate}
        todos={todos.filter(todo => todo.date === selectedDate?.toISOString().split('T')[0])}
      />
    </div>
  );
};

export default Calendar;
